// SM-2 ì•Œê³ ë¦¬ì¦˜ í•µì‹¬ ë¡œì§ ì˜ˆì‹œ
function calculateNextInterval(card, response) {
  const now = new Date();
  let newInterval, newEaseFactor, newStatus;

  // í˜„ì¬ ì¹´ë“œì˜ ìƒíƒœì™€ ì‚¬ìš©ì ì‘ë‹µì— ë”°ë¼ ë‹¤ìŒ ê°„ê²© ê³„ì‚°
  if (card.status === 'new' || card.status === 'learning') {
    if (response === 'again') {
      newInterval = 1/1440; // 1ë¶„ (ì¼ ë‹¨ìœ„ë¡œ í™˜ì‚°)
      newEaseFactor = card.ease_factor;
      newStatus = 'learning';
    } else if (response === 'hard') {
      newInterval = 10/1440; // 10ë¶„
      newEaseFactor = card.ease_factor;
      newStatus = 'learning';
    } else if (response === 'good') {
      newInterval = 1; // 1ì¼
      newEaseFactor = card.ease_factor;
      newStatus = 'review';
    } else if (response === 'easy') {
      newInterval = 4; // 4ì¼
      newEaseFactor = card.ease_factor * 1.3;
      newStatus = 'review';
    }
  } else if (card.status === 'review') {
    if (response === 'again') {
      newInterval = 1; // 1ì¼
      newEaseFactor = Math.max(1.3, card.ease_factor - 0.2);
      newStatus = 'learning';
    } else if (response === 'hard') {
      newInterval = card.interval * 1.2;
      newEaseFactor = Math.max(1.3, card.ease_factor - 0.15);
      newStatus = 'review';
    } else if (response === 'good') {
      newInterval = card.interval * card.ease_factor;
      newEaseFactor = card.ease_factor;
      newStatus = 'review';
    } else if (response === 'easy') {
      newInterval = card.interval * card.ease_factor * 1.3;
      newEaseFactor = card.ease_factor + 0.15;
      newStatus = 'review';
    }
  }

  // ë‹¤ìŒ ë³µìŠµ ë‚ ì§œ ê³„ì‚°
  const nextReviewDate = new Date(now);
  nextReviewDate.setDate(now.getDate() + Math.round(newInterval));

  return {
    interval: newInterval,
    ease_factor: newEaseFactor,
    status: newStatus,
    next_review_date: nextReviewDate
  };
}
