```typescript
// ì˜¤ëŠ˜ì˜ í•™ìŠµ ëª©ë¡ ì¡°íšŒ API ì˜ˆì‹œ
export async function GET(req: Request) {
  const supabase = createServerClient();
  const { searchParams } = new URL(req.url);
  const userId = searchParams.get('userId');

  if (!userId) {
    return NextResponse.json({ error: 'User ID is required' }, { status: 400 });
  }

  // í˜„ì¬ ìš”ì¼ êµ¬í•˜ê¸° (0: ì¼ìš”ì¼, 1: ì›”ìš”ì¼, ...)
  const today = new Date().getDay();
  const dayOfWeek = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'][today];

  try {
    // 1. ì˜¤ëŠ˜ í•™ìŠµí•  í´ë” ì°¾ê¸°
    const { data: folderSchedules } = await supabase
      .from('schedules')
      .select('target_id')
      .eq('user_id', userId)
      .eq('target_type', 'folder')
      .contains('days_of_week', [dayOfWeek]);

    const folderIds = folderSchedules.map(schedule => schedule.target_id);

    // 2. ì˜¤ëŠ˜ í•™ìŠµí•  íŒŒì¼ ì°¾ê¸° (ì§ì ‘ ìŠ¤ì¼€ì¤„ì´ ìˆëŠ” íŒŒì¼ + í´ë” ìŠ¤ì¼€ì¤„ì„ ìƒì†ë°›ëŠ” íŒŒì¼)
    // 2.1. ì§ì ‘ ìŠ¤ì¼€ì¤„ì´ ìˆëŠ” íŒŒì¼
    const { data: fileSchedules } = await supabase
      .from('schedules')
      .select('target_id')
      .eq('user_id', userId)
      .eq('target_type', 'file')
      .contains('days_of_week', [dayOfWeek]);

    const directFileIds = fileSchedules.map(schedule => schedule.target_id);

    // 2.2. í´ë” ìŠ¤ì¼€ì¤„ì„ ìƒì†ë°›ëŠ” íŒŒì¼
    const { data: inheritedFiles } = await supabase
      .from('files')
      .select('id')
      .eq('user_id', userId)
      .in('folder_id', folderIds)
      .not('id', 'in', directFileIds); // ì§ì ‘ ìŠ¤ì¼€ì¤„ì´ ìˆëŠ” íŒŒì¼ ì œì™¸

    const inheritedFileIds = inheritedFiles.map(file => file.id);

    // 3. ëª¨ë“  í•™ìŠµí•  íŒŒì¼ ID í•©ì¹˜ê¸°
    const allFileIds = [...directFileIds, ...inheritedFileIds];

    // 4. íŒŒì¼ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
    const { data: files } = await supabase
      .from('files')
      .select('*')
      .in('id', allFileIds);

    // 5. ê° íŒŒì¼ë³„ ì˜¤ëŠ˜ í•™ìŠµí•  ì¹´ë“œ ìˆ˜ ê³„ì‚°
    const todayStudyList = await Promise.all(files.map(async (file) => {
      const { count: newCount } = await supabase
        .from('card_progress')
        .select('id', { count: 'exact' })
        .eq('file_id', file.id)
        .eq('status', 'new');

      const { count: learningCount } = await supabase
        .from('card_progress')
        .select('id', { count: 'exact' })
        .eq('file_id', file.id)
        .eq('status', 'learning')
        .lte('next_review_date', new Date().toISOString());

      const { count: reviewCount } = await supabase
        .from('card_progress')
        .select('id', { count: 'exact' })
        .eq('file_id', file.id)
        .eq('status', 'review')
        .lte('next_review_date', new Date().toISOString());

      return {
        ...file,
        cardCounts: {
          new: newCount,
          learning: learningCount,
          review: reviewCount,
          total: newCount + learningCount + reviewCount
        }
      };
    }));

    return NextResponse.json(todayStudyList);
  } catch (error) {
    console.error('Error fetching today\'s study list:', error);
    return NextResponse.json({ error: 'Failed to fetch today\'s study list' }, { status: 500 });
  }
}
```

