// ì—°ì† í•™ìŠµì¼ ìˆ˜ ì—…ë°ì´íŠ¸ í•¨ìˆ˜ ì˜ˆì‹œ
async function updateLearningStreak(userId) {
  const supabase = createServerClient();
  const today = new Date().toISOString().split('T')[0]; // YYYY-MM-DD í˜•ì‹

  // í˜„ì¬ ì‚¬ìš©ìì˜ ì—°ì† í•™ìŠµ ì •ë³´ ê°€ì ¸ì˜¤ê¸°
  const { data: streakData } = await supabase
    .from('learning_streaks')
    .select('*')
    .eq('user_id', userId)
    .single();

  if (!streakData) {
    // ì²˜ìŒ í•™ìŠµí•˜ëŠ” ê²½ìš° ìƒˆ ë ˆì½”ë“œ ìƒì„±
    await supabase
      .from('learning_streaks')
      .insert({
        user_id: userId,
        streak_count: 1,
        last_study_date: today
      });
    return 1;
  }

  const lastStudyDate = new Date(streakData.last_study_date);
  const currentDate = new Date(today);

  // ë‚ ì§œ ì°¨ì´ ê³„ì‚° (ë°€ë¦¬ì´ˆ â†’ ì¼)
  const diffTime = Math.abs(currentDate.getTime() - lastStudyDate.getTime());
  const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

  let newStreakCount;

  if (diffDays === 0) {
    // ì˜¤ëŠ˜ ì´ë¯¸ í•™ìŠµí–ˆìŒ (ì¤‘ë³µ í˜¸ì¶œ)
    newStreakCount = streakData.streak_count;
  } else if (diffDays === 1) {
    // ì—°ì†ìœ¼ë¡œ í•™ìŠµí•¨
    newStreakCount = streakData.streak_count + 1;
  } else {
    // í•˜ë£¨ ì´ìƒ ê±´ë„ˆëœ€
    newStreakCount = 1;
  }

  // ì—°ì† í•™ìŠµ ì •ë³´ ì—…ë°ì´íŠ¸
  await supabase
    .from('learning_streaks')
    .update({
      streak_count: newStreakCount,
      last_study_date: today
    })
    .eq('user_id', userId);

  return newStreakCount;
}
